import json

def count_sentence_lengths(jsonl_file):
    sentence_lengths = []
    with open(jsonl_file, 'r', encoding="utf-8") as file:
        for line in file:
            json_obj = json.loads(line)
            if 'content' in json_obj:
                for content_item in json_obj['content']:
                    if 'sentence' in content_item:
                        sentence_lengths.append(len(content_item['sentence']))
    return sentence_lengths

# 使用示例
file_path = 'test.jsonl'
lengths = count_sentence_lengths(file_path)
def count_sentence_length_categories(sentence_lengths, threshold=512):
    less_or_equal = sum(length <= threshold for length in sentence_lengths)
    greater_than = sum(length > threshold for length in sentence_lengths)
    return less_or_equal, greater_than

# 统计长度小于等于512和大于512的个数
less_or_equal_512, greater_than_512 = count_sentence_length_categories(lengths)
print(f"Length <= 512: {less_or_equal_512}, Length > 512: {greater_than_512}")

